export default {
  props: ["heading", "subheading", "grid", "link", "src"],
  components: {
    Grid1,
    Grid2,
    Grid3,
    Grid4,
    Grid5
  },
  data() {
    return {
      html: false,
      css: false,
      js: false,
      bootstrap: false,
      wordpress: false,
      google: false,
      sass: false,
      vuejs: false,
      webpack: false,
